import React,{useState} from 'react'
import { IType } from '../../interfaces/IResponse'
import TypesNavigation from '../../navigation/typesNavigation/typesNavigation'
import { useAddDeviceMutation, useGetBrandsByTypeQuery } from '../../redux/api/server.api'
import './modalAddDevice.scss'

interface modalProps{
  setAddDeviceModal:(set:boolean)=>void,
  types?:IType[]
}

export default function Modal({types,setAddDeviceModal}:modalProps) {

  const [name,setName] = React.useState<string | undefined>()
  const [price,setPrice] = React.useState<number>()
  const [type,setType] = React.useState<number | null>()
  const [brand,setBrand]= React.useState<number | null>()
  const [img,setImg] = React.useState<string | undefined>()
  const {data:Brands,isLoading} = useGetBrandsByTypeQuery(type ? type : 0)
  const [addDevice] = useAddDeviceMutation()


 

  const submitHandler  = (e:React.FormEvent<HTMLInputElement>) =>{
    e.preventDefault()
    const formData:any = new FormData()

    formData.append('name',name)
    formData.append('price',price)
    formData.append('typeId',type)
    formData.append('brandId',brand)
    formData.append('img',img)

    addDevice(formData).unwrap().catch((e)=>console.log(e))
  }
  
  return (
    <div className='modal' onClick={()=>setAddDeviceModal(false)}>
      <div className='modal_content'>
      <form className='modal_content' onClick={(e)=>e.stopPropagation()}>
       <input value={name} onChange={(e:React.ChangeEvent<HTMLInputElement>)=>setName(e.target.value)} className='modal_input' placeholder='Название товара'></input>
       <input className='modal_input' value={price} onChange={(e:React.ChangeEvent<HTMLInputElement>)=>setPrice(Number(e.target.value))} type='number' placeholder='Цена'></input>
       <select className='modal_select'  onChange={(e:React.ChangeEvent<HTMLSelectElement>)=>setType(Number(e.target.value))}>
       <option selected disabled={true}>Type</option>
          {types?.map((el:any)=>
          <option key={el.id} value={el.id} >{el.name}</option>
          )}
       </select>
       {type ? 
        <select className='modal_select'  onChange={(e)=>setBrand(Number(e.target.value))}>
        <option selected disabled={true}>Brand</option>
         {isLoading 
         ? <h1>Загрузка...</h1>
         :
         Brands?.map((el)=>
         <option  value={el.id}>
          {el.name}
         </option>
         ) 
         }
       </select>
       :
       ''
}
       <input type='file' onChange={(e:any)=>setImg(e.target.files[0])}></input>
       <input type='submit' className='submit' onClick={submitHandler}></input>
       </form>
      </div>
    </div>
  )
}
