import { createSlice,PayloadAction } from "@reduxjs/toolkit";

const initialState = {
    type:null
}

const typeSlice = createSlice({
    name:"type",
    initialState,
    reducers:{
        setType(state,action){
         state.type = action.payload
        }
    }
})


export const{setType} = typeSlice.actions

export default typeSlice.reducer
