import React, { useContext, useRef, useState } from 'react'
import styles from './StartPage.module.css'
import { Link } from 'react-router-dom'
import Monitor from '../../components/images/categories image/Monitor.svg'
import Computer from '../../components/images/categories image/Computer.svg'
import VideoCard from '../../components/images/categories image/videocard.svg'
import Mouse from '../../components/images/categories image/Mouse.svg'
import Processor from '../../components/images/categories image/Processor.svg'
import OZU from '../../components/images/categories image/OZU.svg'
import MotherBoard from '../../components/images/categories image/motherboard.svg'
import BP from '../../components/images/categories image/BP.svg'

export default function StartPage() {
    const {type,setType} = useState('312')
    const {PageItem,setPageItem} = useState('321')
    const [categories,SetCategories] = useState([
        {id:1, name:'Компьютеры',photo:Computer,type:'Computer'},
        {id:2, name:'Мониторы',photo:Monitor,type:'Monitor'},
        {id:3, name:'Видеокарты',photo:VideoCard,type:'VideoCard'},
        {id:4, name:'Переферийные устройства',photo:Mouse,type:'Pereferiya'},
        {id:5, name:'Процессоры',photo:Processor,type:'CPU'},
        {id:6, name:'Оперативная память',photo:OZU,type:'OZU'},
        {id:7, name:'Материнские платы',photo:MotherBoard,type:'MotherBoard'},
        {id:8, name:'Блоки питания',photo:BP,type:'PowerBlock'},
    ])
    const OnMouseOver = (e) =>{
        e.target.classList.add(styles.active)
        console.log(e.target)
    }
    const OnMouseOut = (e) =>{
        e.target.classList.remove(styles.active)
    }
  return (
    <div  className={styles.StartPage}>
      <div className={styles.Cotegories} >
        {categories.map((category,key)=>
            <Link to='/store' key={key + 1} style={{textDecoration:'none'}}><div id='all' className={styles.AllCategories} onMouseOver={OnMouseOver} onMouseOut={OnMouseOut}  onClick={()=>{setType(category.type)
              localStorage.setItem('type',category.type)
            }}>{category.name}<img  onMouseOver={OnMouseOver} onMouseOut={OnMouseOut} className={styles.AllPhoto} src={category.photo}></img></div></Link>
        )}
      </div>
    </div>
  )
}
