import React, { useContext, useState, useEffect } from 'react'
import Pagination from '../../components/UI/Pagination/Pagination'
import { ComputerStore } from './store'
import '../../components/Alerts/Alert.css'
import styles from './storePage.module.css'
import AddToBucket from '../../components/UI/Buttons/addToBucket/AddToBucket'
import sadSmile from '../../components/images/ArrayEmpty/sadSmile.svg'
import SuccesBucketAdd from '../../components/Alerts/SuccesBucketAdd/SuccesBucketAdd'
import { CSSTransition } from 'react-transition-group'
import {Link} from 'react-router-dom'
import Search from '../../components/UI/Search/Search'
import { useGetDevicesQuery, useGetTypesQuery } from '../../components/redux/api/server.api'
import Loader from '../../components/UI/Loader/Loader'
export default function StorePage() {
    const [type,setType] = useState()
    const [brand,setBrand] = useState()
    const {data:Devices,isLoading}:any = useGetDevicesQuery({type:type ? type : '',brand:brand ? brand : ''})
    const {data:Types}:any = useGetTypesQuery('')
    const [store,setStore]:any = useState([])
    const [currentPage,setCurrentPAge] = useState(1)
    const [ItemsPerPage] = useState(4)
    const LastItemIndex = currentPage * ItemsPerPage
    const FirstItemIndex = LastItemIndex - ItemsPerPage
   
    const paginate = (pageNumber:any) => setCurrentPAge(pageNumber)
    

    const OnMouseOver = (e:any) =>{
      e.target.classList.add(styles.active)
  }
  const OnMouseOut = (e:any) =>{
      e.target.classList.remove(styles.active)
  }

 
 
  
 if(isLoading){
  return <Loader/>
 }
  return (
    <div className={styles.StorePage}>
      <CSSTransition   timeout={300} classNames='transition' unmountOnExit>
        
        <SuccesBucketAdd/>

      </CSSTransition> 
   
        {Types.map((el:any)=>
        <h1>{el.id}</h1>
        )}

        {Devices.rows.length <= 0 
        ? 
        <div className={styles.arrEmpty}><h1>Товары не обнаружены</h1> <img src ={sadSmile}></img></div>
        : 
        <div className={styles.div}> {Devices.rows.map((item:any,key:any)=>
          <Link to={`/ItemPage/${item.id}`} key ={key + 1} style={{textDecoration:'none',display:'block'}}>
            <div id = {item.id} onMouseOut={OnMouseOut} onMouseOver={OnMouseOver} className={styles.AllItems}>
                <img className = {styles.itemPhoto} src ={'http://localhost:5000/' + item.img}/>
                <div className={styles.itemText}>{item.text}</div>
                <div id = {item.id} className={styles.itemName}>{item.name}</div>
                <div className = {styles.itemCost}>{item.price}$</div>
                <Link to ='' style={{pointerEvents:'none'}}><AddToBucket id = {item.id} /></Link>
                </div>
                </Link>
        )}</div>}
        <div className={styles.Paginations}><Pagination ItemsPerPage={ItemsPerPage} totalItems={store.length} Pagination ={paginate} currentPage={currentPage}/></div>

    </div>
  )
}
