import React from 'react'
import styles from './AddToBucket.module.css'
import BucketButton from '../../../images/bucketButton.svg'
export default function AddToBucket({BucketAdd,id}:any) {
    const OnMouseOver = (e:any) =>{
        e.target.classList.add(styles.active)
    }
    const OnMouseOut = (e:any) =>{
        e.target.classList.remove(styles.active)
    }
  return (
    <button onClick={BucketAdd} onMouseOver={OnMouseOver} onMouseOut={OnMouseOut} className={styles.button} id={id}>Добавить
    <img src={BucketButton}></img>
    </button>
  )
}
