import React, { useContext, useEffect, useState } from 'react'
import styles from './Search.module.css'
import { ComputerStore } from '../../../pages/mainPage/store'

import {Link} from 'react-router-dom'
export default function Search() {
  const [searchValue,setSearchValue]:any = useState()
  const {PageItem,setPageItem}:any= useState('312')
   

  const FiltredStore = ComputerStore.filter(item=>{
    return item.name?.toLowerCase().includes(searchValue?.toLowerCase())
  })
  const IDforItemPage = (e:any) =>{
    setPageItem(e.target.id)
    localStorage.setItem('pageid',e.target.id)
  }

  const OnMouseOver = (e:any) =>{
    e.target.classList.add(styles.active)
}
const OnMouseOut = (e:any) =>{
    e.target.classList.remove(styles.active)
}

  return (
    <div className={styles.search}>
    <input placeholder='Поиск товара' onChange={(e)=>setSearchValue(e.target.value)} className={styles.searchQuerry}>
    </input>
    <div className={styles.searchList}>
    {searchValue?.length <= 0 
    
    ?
    ''
    :
    searchValue?.length <=0
    
    ?
    
    <p>Товары не найдены</p>
    
    :
  
    FiltredStore?.map((item)=>
    <Link style={{textDecoration:'none',color:'black'}} to={`/ItemPage/${item.id}`}><div onMouseOut={OnMouseOut} onMouseOver={OnMouseOver} id={item.id} onClick={IDforItemPage} className={styles.itemName}>
      {item.name}
      </div></Link>


    )}
    </div>
    </div>
  )
}
