import React from 'react'
import { useChangeDeviceMutation, useGetBasketQuery, useGetBrandsQuery, useGetTypesQuery } from '../../redux/api/server.api'
import styles from './modalChangeDevice.module.scss'

interface modalProps{
    setModalChange:(value:boolean)=>void,
    currentItem:{
        id:number
        name:string,
        price:number,
    },
}

export default function ModalChangeDevice({setModalChange,currentItem}:modalProps) {

  const [changeDevice] = useChangeDeviceMutation()
  const {data:Types} = useGetTypesQuery('')
  const {data:Brands} = useGetBrandsQuery('')

  return (
    <div className={styles.modal} onClick={()=>setModalChange(false)}>
        <div className={styles.modal_content} onClick={(e)=>e.stopPropagation()}>
         <input value={currentItem.name}></input>
         <input value={currentItem.price}></input>
         <input value={currentItem.name}></input>
         <select defaultValue={currentItem.TypeName}>{Types?.map((el)=>
         <option>{el.name}</option>
         )}</select>
         <select></select>

        <input type='submit' value='Изменить'></input>
        </div>
      
    </div>
  )
}
