import React,{useState} from 'react'
import TypesNavigation from '../../navigation/typesNavigation/typesNavigation'
import { useAddDeviceMutation, useGetBrandsByTypeQuery } from '../../redux/api/server.api'
import './modalAddDevice.scss'

interface modalProps{
  addDeviceModal:boolean,
  setAddDeviceModal:(set:boolean)=>void,
  types:any
}

export default function Modal({types,addDeviceModal,setAddDeviceModal}:modalProps) {

  const [name,setName] = useState()
  const [price,setPrice] = useState()
  const [type,setType] = useState()
  const [brand,setBrand] = useState()
  const [img,setImg] = useState()
  const formData = new FormData()
  const {data:Brands,isLoading} = useGetBrandsByTypeQuery(type ? type : 0)
  const [addDevice] = useAddDeviceMutation()


 

  const submitHandler = (e:any) =>{
    e.preventDefault()
    addDevice({name:name,price:price,brandId:brand,typeId:type,img:img})
  }

  return (
    <div className='modal' onClick={()=>setAddDeviceModal(false)}>
      <div className='modal_content'>
      <form className='modal_content' onClick={(e)=>e.stopPropagation()}>
       <input value={name} onChange={(e:any)=>setName(e.target.value)} className='modal_input' placeholder='Название товара'></input>
       <input className='modal_input' value={price} onChange={(e:any)=>setPrice(e.target.value)} type='number' placeholder='Цена'></input>
       <select className='modal_select'  onChange={(e:any)=>setType(e.target.value)}>
          {types.map((el:any)=>
          <option key={el.id} value={el.id} >{el.name}</option>
          )}
       </select>
         
       <select className='modal_select' onChange={(e:any)=>setBrand(e.target.value)}>
         {isLoading 
         ? <option>'Загрузка'</option> 
         :Brands.map((el:any)=>
         <option value={el.id}>
          {el.name}
         </option>
         ) 
         }
       </select>
       <input type='file' onChange={(e:any)=>setImg(e.target.files[0])}></input>
       <input type='submit' onClick={submitHandler}></input>
       </form>
      </div>
    </div>
  )
}
