import React,{useState} from 'react'
import { useAddBrandMutation } from '../../redux/api/server.api'
import './modalAddBrand.scss'

interface modalProps{
    setAddBrandModal:(smth:boolean)=>void
}

export default function ModalAddBrand({setAddBrandModal}:modalProps) {
    
    const [addBrand] = useAddBrandMutation()
    const [name,setName] = useState('')
    
    const submitHandler = (e:any) =>{
        e.preventDefault()
        addBrand({name:name})
        setAddBrandModal(false)
    }

  return (
    <div className='modal' onClick={()=>setAddBrandModal(false)}>
    <div onClick={(e)=>e.stopPropagation()} className='modal_content'>
    <input onChange={(e)=>setName(e.target.value)} className='modal_input'></input>
    <input type='submit' onClick={submitHandler}/>
    </div>
    </div>
  )
}
