import React from 'react'
import { useChangeDeviceMutation, useGetBasketQuery, useGetBrandsByTypeQuery, useGetBrandsQuery, useGetTypesQuery } from '../../redux/api/server.api'
import Loader from '../../UI/Loader/Loader'
import styles from './modalChangeDevice.module.scss'

interface modalProps{
    setModalChange:(value:boolean)=>void,
    currentItem:{
        TypeName:string,
        typeId:number,
        brandId:number,
        id:number
        name:string,
        price:number,
    },
    Types:[
        {
            id:number,
            name:string,
        }
    ]
}

export default function ModalChangeDevice({setModalChange,currentItem,Types}:modalProps) {

  const [changeDevice] = useChangeDeviceMutation()
  const {data:Brands,isLoading:brandsIsLoading} = useGetBrandsByTypeQuery(currentItem.typeId)
  
  if(brandsIsLoading){
    return <Loader/>
  }

  return (
    <div className={styles.modal} onClick={()=>setModalChange(false)}>
        <div className={styles.modal_content} onClick={(e)=>e.stopPropagation()}>
         <input value={currentItem.name}></input>
         <input value={currentItem.price}></input>
         <input value={currentItem.name}></input>
         <select>
        {Types?.map((el)=>
         <option selected={el.id == currentItem.typeId ? true : false}>{el.name}</option>
         )}
         </select>
         <select>
            {Brands?.map((el)=>
            <option selected={el.id == currentItem.brandId ? true : false}>
             {el.name}
            </option>
            )}
         </select>

        <input type='submit' value='Изменить'></input>
        </div>
      
    </div>
  )
}
